import "./Ownable.sol";
pragma solidity 0.5.12;

contract Coinflip is Ownable{

  event coinflipResult(uint result, uint winnings);
  event uncoughtException(uint step);

  uint public balance;

  function random() public returns (uint) {
    uint res = now % 2;
    //emit coinflipResult(res);
    return res;
  }

  function coinFlip() public payable returns(bool, uint){
    uint bet_value = msg.value;
    bool winner;
    uint winnings = 0;
    require(msg.value <= balance, "Insufficient contract balance");
    uint res = block.timestamp % 2;
    if(res == 0){
      winner = false;
      balance = balance + bet_value;
      emit coinflipResult(res, winnings);
    }
    else if(res == 1){
      winner = true;
      winnings = 2* bet_value;
      balance = balance - bet_value;
      msg.sender.transfer(winnings);
      emit coinflipResult(res, winnings);
    }
    else{
      emit uncoughtException(1);
    }
    return (winner, winnings);
  }

  function depositeBalance() public onlyOwner payable returns(uint) {
      uint newBalance = balance + msg.value;
      balance = newBalance;
      return newBalance;
  }

  function withdrawAll() public onlyOwner returns(uint) {
      uint toTransfer = balance;
      balance = 0;
      msg.sender.transfer(toTransfer);
      return toTransfer;
  }

  function withdrawPercentage(uint percentage) public onlyOwner returns(uint) {
      uint toTransfer = (balance * (percentage/100));
      balance = 0;
      msg.sender.transfer(toTransfer);
      return toTransfer;
  }
}
